const menu=document.querySelector(".menu"),hamburger=document.querySelector(".header__menu-hamburger"),xIcon=document.querySelector(".xIcon"),menuIcon=document.querySelector(".ham");function toggleMenu(){menu.classList.contains("showMenu")?(menu.classList.remove("showMenu"),xIcon.style.display="none",menuIcon.style.display="block"):(menu.classList.add("showMenu"),xIcon.style.display="block",menuIcon.style.display="none")}hamburger.addEventListener("click",toggleMenu);const menuLinks=document.querySelectorAll(".menuLink");menuLinks.forEach(function(e){e.addEventListener("click",toggleMenu)});